// InventoryForm.jsx
import React, { useEffect, useState } from 'react';
import { Button } from '@mui/material';
import InventoryModal from '../components/inventory/InventoryModal';
import { AddInventory, getInventory } from '../Api/Api';
import InventoryList from '../components/InventoryList';

const Inventory = () => {
    const [formData, setFormData] = useState(initialState)
    const [isModalOpen, setModalOpen] = useState(false);
    const [dataList, setInventoryList] = useState([])
    const toggleModal = () => {
        setModalOpen(prevState => !prevState)
    }

    const saveInventory = async (data) => {
        try {
            const response = await AddInventory(data);
            alert(response.message);
            toggleModal();
            fetchInventoryList(); // Fetch updated list
        } catch (error) {
            console.error("AddInventory Error:", error.response?.data || error.message);
            alert("Failed to save inventory: " + (error.response?.data?.message || "Unknown error"));
        }
    };
    const fetchInventoryList = async () => {
        const result = await getInventory();
        setInventoryList(result?.data || []);
    };

    useEffect(() => {
        fetchInventoryList()
    }, [])


    const handleGetInventory = async (id) => {
        const item = await getOneInventory(id)
        if (!item || !item.data) {
            console.warn('item not found for ID:', id, item);
            return;
        }
        console.log('item is getting', item);
        setFormData({ ...item.data, id: id });
    };
    return (
        <div style={{ padding: 20 }}>
            <Button variant="contained" onClick={() => setModalOpen(true)}>
                + Add New Purchase Entry
            </Button>
            <InventoryList dataList={dataList} handleGetInventory={handleGetInventory} />
            <InventoryModal isOpen={isModalOpen} onClose={toggleModal} saveInventory={saveInventory} formData={formData} />




        </div>
    );
};

export default Inventory;
