// InventoryForm.jsx
import React, { useState } from 'react';
import { Button } from '@mui/material';
import InventoryModal from '../components/inventory/InventoryModal';
import { AddInventory } from '../Api/Api';

const Inventory = () => {
    const [isModalOpen, setModalOpen] = useState(false);
    const [resetFormKey, setResetFormKey] = useState(0);

    const toggleModal = () => {
        setModalOpen(prevState => !prevState)
    }
    const saveInventory = async (data) => {
        console.log("Sending data:", data); // Debug
        try {
            const response = await AddInventory(data);
            alert(response.message);
            toggleModal();
            setResetFormKey(prev => prev + 1);
        } catch (error) {
            console.error("AddInventory Error:", error.response?.data || error.message);
            alert("Failed to save inventory: " + (error.response?.data?.message || "Unknown error"));
        }
    }
    return (
        <div style={{ padding: 20 }}>
            <Button variant="contained" onClick={() => setModalOpen(true)}>
                + Add New Purchase Entry
            </Button>

            <InventoryModal isOpen={isModalOpen} onClose={toggleModal} saveInventory={saveInventory} key={resetFormKey} />




        </div>
    );
};

export default Inventory;
