import {
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    TextField,
    DialogTitle,
    Button,
    InputAdornment,
    FormLabel,
    FormControlLabel,
    RadioGroup,
    Radio,
    Box,
    Typography
} from '@mui/material';
import { useEffect, useState } from 'react';
import { AddOrders, getAllOrders, getInventory } from '../Api/Api';
import OrderList from '../components/Order/OrderList';

const OrderState = {
    Item: '',
    inventoryId: null,
    price: '',
    Ordertype: '',
    quantity: '',
    customername: '',
    customerlocation: '',
    paymentstatus: '',
    orderstatus: "ORDER_PENDING",
    customerphone: ''
};

const OrderPage = () => {
    const [dataList, setDataList] = useState([]);
    const [selectedOrder, setSelectOrder] = useState(OrderState);
    const [Orderlist, setOrders] = useState([]);

    useEffect(() => {
        const fetchInventory = async () => {
            const result = await getInventory();
            setDataList(result?.data || []);
        };
        fetchInventory();
    }, []);

    useEffect(() => {
        fetchOrder()
    }, [])
    const fetchOrder = async () => {
        // If you don't need to show previous orders in the form, skip setting selectedOrder here
        const result = await getAllOrders();
        // maybe log or set them to a separate state
        setOrders(result?.data || []);
    };
    const handleSelect = (e) => {
        const { name, value } = e.target;

        if (name === 'Item') {
            // Find full inventory object based on inventoryName
            const selectedItem = dataList.find(item => item.inventoryName === value);

            setSelectOrder((prev) => ({
                ...prev,
                Item: selectedItem.inventoryName,
                inventoryId: selectedItem.id,
                price: selectedItem.price,
                unit: selectedItem.unit,// optional, for orderTotalAmount
                availableQuantity: selectedItem.availableQuantity
            }));
        } else {
            setSelectOrder((prev) => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handlesave = async () => {
        const {
            inventoryId,
            Item,
            price,
            Ordertype,
            quantity,
            customername,
            customerlocation,
            paymentstatus,
            customerphone,
            unit
        } = selectedOrder;

        // Validation (optional but good)
        if (!inventoryId || !Item || !Ordertype || !quantity || !customername || !customerlocation || !paymentstatus || !customerphone) {
            alert("Please fill in all fields before submitting.");
            return;
        }

        const availableQty = Number(selectedOrder.availableQuantity);
        const requestedQty = Number(selectedOrder.quantity);
        if (requestedQty > availableQty) {
            alert("Available quantity is lesser than the requested quantity. Please adjust the quantity.");
            return; // stop form submission
        }
        let orderstatus = 'ORDER_PENDING';
        if (Ordertype === 'sell' && paymentstatus === 'PAYMENT_COMPLETED') {
            orderstatus = 'ORDER_COMPLETED';
        }
        console.log("Calculated Order Status:", orderstatus);
        const payload = {
            inventoryid: inventoryId,
            inventoryname: Item,
            unit: unit || 'unit',
            ordertype: Ordertype,
            price: price,
            quantity: quantity,
            customername: customername,
            customerlocation: customerlocation,
            paymentstatus: paymentstatus.toLowerCase(), // safe lowercase
            orderstatus: 'orderstatus',
            customerphone: customerphone
        };


        try {
            const response = await AddOrders(payload);
            console.log("Order Placed:", response.data);
            alert("Order placed successfully!");
            fetchOrder()
        } catch (err) {
            console.error("placing Order Error:", err.response?.data || err.message);
            alert("Failed to place order. Check console for details.");
        }
    };



    return (
        <div>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', gap: 3 }}>

                <Box sx={{ width: '50%', border: 1, padding: 2 }}>
                    <h2>Place New Order</h2>
                    <DialogTitle>Product Details</DialogTitle>

                    <FormControl size="small" sx={{ width: 250, mb: 4, marginLeft: 2, }}>
                        <InputLabel id="item-label">Item</InputLabel >
                        <Select
                            name="Item"
                            value={selectedOrder.Item} // You missed this
                            onChange={handleSelect}
                        >
                            {dataList?.map((item) => (
                                <MenuItem key={item.id} value={item.inventoryName}>
                                    {item.inventoryName}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl >

                    {/* Order Type (Sell / Rent) */}

                    <FormControl sx={{ padding: 1, width: 230, marginLeft: 5 }}>
                        <FormLabel id="order-type-label">Type</FormLabel>
                        <RadioGroup
                            row
                            aria-labelledby="order-type-label"
                            name="Ordertype"
                            value={selectedOrder.Ordertype}
                            onChange={handleSelect}
                        >
                            <FormControlLabel value="sell" control={<Radio />} label="Sell" />
                            <FormControlLabel value="rent" control={<Radio />} label="Rent" />
                        </RadioGroup>
                    </FormControl>

                    <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>

                        <TextField
                            sx={{ width: 250, marginLeft: 2, marginTop: 2 }}
                            label="Quantity"
                            name="quantity"
                            type="number"
                            value={selectedOrder.quantity}
                            onChange={handleSelect}
                            InputProps={{
                                endAdornment: (
                                    <InputAdornment position="end">
                                        {selectedOrder.unit}
                                    </InputAdornment>
                                )
                            }}
                            helperText={
                                selectedOrder.availableQuantity
                                    ? `Available quantity is ${selectedOrder.availableQuantity}`
                                    : ''
                            }
                        />

                        {/* Payment Status Dropdown */}

                        <FormControl sx={{ width: 250, marginLeft: 3, marginTop: 2 }}>
                            <InputLabel id="paymentstatus-label">Payment Status</InputLabel>
                            <Select
                                labelId="paymentstatus-label"
                                name="paymentstatus"
                                value={selectedOrder.paymentstatus}
                                onChange={handleSelect}
                            >
                                <MenuItem value="PAYMENT_COMPLETED">Payment completed</MenuItem>
                                <MenuItem value="PAYMENT_PENDING">Payment peding</MenuItem>
                                <MenuItem value="PARTIALLY_PAID">Partially paid</MenuItem>
                                <MenuItem value="PAYMENT_FAILED">Payment failed</MenuItem>
                            </Select>
                        </FormControl>


                    </Box>


                    <DialogTitle>Customer Details</DialogTitle>

                    <TextField sx={{ width: 250, marginLeft: 2, marginBottom: 2, padding: 1, }}
                        label="Customer Name"
                        name="customername"
                        value={selectedOrder.customername}
                        onChange={handleSelect}

                    />
                    <TextField
                        label=" Phone Number"
                        name="customerphone"
                        type='number'
                        value={selectedOrder.customerphone}
                        onChange={handleSelect}
                        sx={{ width: 250, marginLeft: 2, marginBottom: 2, padding: 1, }}
                    />
                    <br />
                    <TextField
                        label="Location"
                        name="customerlocation"
                        value={selectedOrder.customerlocation}
                        onChange={handleSelect}
                        sx={{ width: 250, marginLeft: 2, marginBottom: 2, padding: 1, }}
                    />

                    <FormControl sx={{ width: 250, marginLeft: 3, marginTop: 1 }}>
                        <InputLabel id="orderStatus">Order Status</InputLabel>
                        <Select
                            labelId="orderStatus"
                            name="status"  // 👈 Change here
                            value={selectedOrder.status} // 👈 And here
                            onChange={handleSelect}
                        >
                            <MenuItem value="ORDER_PENDING">Order Pending</MenuItem>
                            <MenuItem value="ORDER_COMPLETED">Order Completed</MenuItem>
                            <MenuItem value="ORDER_CANCELLED">Order Cancelled</MenuItem>
                        </Select>
                    </FormControl>
                    <Button sx={{ width: 250, marginLeft: 2, marginBottom: 2, padding: 1, marginTop: 2, alignItems: 'right' }} variant="contained" onClick={handlesave}>save</Button>

                </Box>
                <Box sx={{ width: '50%', border: 1, padding: 2 }}>
                    <Typography variant="h6">All Orders</Typography>
                    <OrderList Orderlist={Orderlist} />
                </Box>

            </Box>

        </div >
    );
};

export default OrderPage;
