import {
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    TextField,
    DialogTitle,
    Button,
    InputAdornment,
    FormLabel,
    FormControlLabel,
    RadioGroup,
    Radio,
    Box,
    Grid,
    Typography
} from '@mui/material';
import { useEffect, useState } from 'react';
import { AddOrders, getAllOrders, getInventory, updateOrder } from '../Api/Api';

const OrderState = {
    Item: '',
    inventoryId: null,
    price: '',
    Ordertype: '',
    quantity: '',
    customername: '',
    customerlocation: '',
    paymentstatus: '',
    orderstatus: '',
    customerphone: ''
};

const OrderPage = () => {
    const [dataList, setDataList] = useState([]);
    const [selectedOrder, setSelectOrder] = useState(OrderState);

    useEffect(() => {
        const fetchInventory = async () => {
            const result = await getInventory();
            setDataList(result?.data || []);
        };
        fetchInventory();
    }, []);

    useEffect(() => {
        fetchOrder()
    }, [])
    const fetchOrder = async () => {
        // If you don't need to show previous orders in the form, skip setting selectedOrder here
        const result = await getAllOrders();
        // maybe log or set them to a separate state
        console.log(result?.data);
    };
    const handleSelect = (e) => {
        const { name, value } = e.target;

        if (name === 'Item') {
            // Find full inventory object based on inventoryName
            const selectedItem = dataList.find(item => item.inventoryName === value);

            setSelectOrder((prev) => ({
                ...prev,
                Item: selectedItem.inventoryName,
                inventoryId: selectedItem.id,
                price: selectedItem.price,
                unit: selectedItem.unit // optional, for orderTotalAmount
            }));
        } else {
            setSelectOrder((prev) => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handlesave = async () => {
        const {
            inventoryId,
            Item,
            price,
            Ordertype,
            quantity,
            customername,
            customerlocation,
            paymentstatus,
            customerphone,
            unit
        } = selectedOrder;

        // Validation (optional but good)
        if (!inventoryId || !Item || !Ordertype || !quantity || !customername || !customerlocation || !paymentstatus || !customerphone) {
            alert("Please fill in all fields before submitting.");
            return;
        }

        const payload = {
            inventoryid: inventoryId,
            inventoryname: Item,
            unit: unit || 'unit',
            ordertype: Ordertype,
            price: price,
            quantity: quantity,
            customername: customername,
            customerlocation: customerlocation,
            paymentstatus: paymentstatus.toLowerCase(), // safe lowercase
            orderstatus: 'pending',
            customerphone: customerphone
        };


        try {
            const response = await AddOrders(payload);
            console.log("Order Placed:", response.data);
            alert("Order placed successfully!");
        } catch (err) {
            console.error("placing Order Error:", err.response?.data || err.message);
            alert("Failed to place order. Check console for details.");
        }
    };

    return (
        <Box sx={{ p: 4 }}>
            <Typography variant="h5" gutterBottom>
                Create Order
            </Typography>

            <Grid container spacing={2}>
                {/* Product Details */}
                <Grid item xs={12}>
                    <Typography variant="subtitle1" gutterBottom>
                        Product Details
                    </Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                    <FormControl fullWidth size="small">
                        <InputLabel id="item-label">Item</InputLabel>
                        <Select
                            labelId="item-label"
                            name="Item"
                            value={selectedOrder.Item}
                            onChange={handleSelect}
                            label="Item"
                        >
                            {dataList.map((item) => (
                                <MenuItem key={item.id} value={item.inventoryName}>
                                    {item.inventoryName}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </Grid>

                <Grid item xs={12} sm={6}>
                    <FormControl component="fieldset" fullWidth>
                        <FormLabel component="legend">Order Type</FormLabel>
                        <RadioGroup
                            row
                            name="Ordertype"
                            value={selectedOrder.Ordertype}
                            onChange={handleSelect}
                        >
                            <FormControlLabel value="sell" control={<Radio />} label="Sell" />
                            <FormControlLabel value="rent" control={<Radio />} label="Rent" />
                        </RadioGroup>
                    </FormControl>
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField
                        fullWidth
                        label="Quantity"
                        name="quantity"
                        type="number"
                        value={selectedOrder.quantity}
                        onChange={handleSelect}
                        InputProps={{
                            endAdornment: (
                                <InputAdornment position="end">{selectedOrder.unit}</InputAdornment>
                            )
                        }}
                    />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField
                        fullWidth
                        label="Unit"
                        name="unit"
                        value={selectedOrder.unit || ''}
                        onChange={handleSelect}
                    />
                </Grid>

                {/* Customer Details */}
                <Grid item xs={12}>
                    <Typography variant="subtitle1" gutterBottom>
                        Customer Details
                    </Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField
                        fullWidth
                        label="Customer Name"
                        name="customername"
                        value={selectedOrder.customername}
                        onChange={handleSelect}
                    />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField
                        fullWidth
                        label="Phone Number"
                        name="customerphone"
                        type="number"
                        value={selectedOrder.customerphone}
                        onChange={handleSelect}
                    />
                </Grid>

                <Grid item xs={12}>
                    <TextField
                        fullWidth
                        label="Location"
                        name="customerlocation"
                        value={selectedOrder.customerlocation}
                        onChange={handleSelect}
                    />
                </Grid>

                <Grid item xs={12}>
                    <FormControl fullWidth>
                        <InputLabel id="paymentstatus-label">Payment Status</InputLabel>
                        <Select
                            labelId="paymentstatus-label"
                            name="paymentstatus"
                            value={selectedOrder.paymentstatus}
                            onChange={handleSelect}
                            label="Payment Status"
                        >
                            <MenuItem value="completed">Paid</MenuItem>
                            <MenuItem value="pending">Pending</MenuItem>
                            <MenuItem value="cancelled">Cancelled</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>

                {/* Save Button */}
                <Grid item xs={12} sx={{ textAlign: "right" }}>
                    <Button
                        variant="contained"
                        onClick={handlesave}
                        color="primary"
                        size="large"
                    >
                        Save
                    </Button>
                </Grid>
            </Grid>
        </Box>


    );
};

export default OrderPage;
