// InventoryForm.jsx
import React, { useState } from 'react';
import { Button } from '@mui/material';
import CustomModal from '../components/InventoryModal';
import CustomForm from '../components/CustomForm';
import InventoryModal from '../components/InventoryModal';
const formFields = [
    { name: 'inventoryName', label: 'Inventory Name' },
    { name: 'category', label: 'Category', type: 'select', options: ['Fertilizer', 'Seeds', 'Pesticides', 'Herbicides', 'Farming Tools', 'GreenHouse shades and nets', 'Gardening ports'] },
    { name: 'price', label: 'Price', inputType: 'number' },
    { name: 'unit', label: 'Unit', type: 'select', options: ['kg', 'g', 'litre', 'pack', 'bottle', 'ton'] },
    { name: 'source', label: 'Source' },
    { name: 'sourceCompany', label: 'Source Company', type: 'select', options: ['T.stanes&company', 'Madayikkan', 'J J Tools', 'Priya Agencies'] },
    { name: 'availableQuantity', label: 'Available Quantity', inputType: 'number' },
    { name: 'paymentStatus', label: 'Payment Status', type: 'select', options: ['Paid', 'Pending'] }
]
const InventoryForm = () => {
    const [isModalOpen, setModalOpen] = useState(false);

    const toggleModal = () => {
        setModalOpen(prevState => !prevState)
    }


    return (
        <div style={{ padding: 20 }}>
            <Button variant="contained" onClick={() => setModalOpen(true)}>
                + Add New Purchase Entry
            </Button>

            <InventoryModal isOpen={isModalOpen} onClose={toggleModal} />




        </div>
    );
};

export default InventoryForm;
