import {
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    TextField
} from '@mui/material';
import { useEffect, useState } from 'react';
import { getInventory } from '../Api/Api';

const OrderState = {
    Item: '',
    OrderType: '',
    Quantity: '',
    CustomerName: '',
    CustomerPhoneNum: '',
    CustomerLocation: '',
};

const OrderPage = () => {
    const [dataList, setDataList] = useState([]);
    const [selectedOrder, setSelectOrder] = useState(OrderState);

    useEffect(() => {
        const fetchInventory = async () => {
            const result = await getInventory();
            setDataList(result?.data || []);
        };
        fetchInventory();
    }, []);

    const handleSelect = (e) => {
        const { name, value } = e.target;
        setSelectOrder((prev) => ({
            ...prev,
            [name]: value
        }));
        console.log("Updated selectedOrder:", {
            ...selectedOrder,
            [name]: value
        });
    };

    return (
        <div>
            {/* Select Item */}
            <FormControl size="small" sx={{ width: 300, mb: 2 }}>
                <InputLabel id="item-label">Item</InputLabel>
                <Select
                    labelId="item-label"
                    name="Item"
                    value={selectedOrder.Item} // You missed this
                    onChange={handleSelect}
                >
                    {dataList?.map((item) => (
                        <MenuItem key={item.id} value={item.inventoryName}>
                            {item.inventoryName}
                        </MenuItem>
                    ))}
                </Select>
            </FormControl>

            {/* Select Order Type */}
            <FormControl size="small" sx={{ width: 300 }}>
                <InputLabel id="order-type-label">Order Type</InputLabel>
                <Select
                    labelId="order-type-label"
                    name="OrderType"
                    value={selectedOrder.OrderType}
                    onChange={handleSelect}
                >
                    <MenuItem value="Sell">Sell</MenuItem>
                    <MenuItem value="Rent">Rent</MenuItem>
                </Select>
            </FormControl>
            <br />
            <TextField
                label="Quantity"
                name="Quantity"
                type="number"
                value={selectedOrder.Quantity}
                onChange={handleSelect}
                sx={{ width: 600 }}
            />

        </div>
    );
};

export default OrderPage;
