import {
    Select,
    MenuItem,
    FormControl,
    InputLabel
} from '@mui/material';
import { useEffect, useState } from 'react';
import { getInventory } from '../Api/Api';
const OrderState = {
    Item: '',
    OrderType: '',
    Quantity: '',
    CustomerName: '',
    CustomerPhoneNum: '',
    CustomerLocation: '',

}

const OrderPage = () => {
    const [dataList, setDataList] = useState([]);
    const [selectedOrder, setSelectOrder] = useState(OrderState)

    useEffect(() => {
        const fetchInventory = async () => {
            const result = await getInventory();
            setDataList(result?.data || []);
        };
        fetchInventory();
    }, []);

    const handleSelect = (e) => {
        const { name, value } = e.target;
        setSelectOrder((prev) => ({
            ...prev,
            [name]: value
        }));
        console.log("item in field", selectedOrder)
    }

    return (
        <FormControl size="small" sx={{ width: 300 }}>
            <InputLabel id="item-label">Item</InputLabel>
            <Select
                labelId="item-label"
                name="Item"
                onChange={handleSelect}
            >
                {dataList?.map((item) => (
                    <MenuItem key={item.id} value={item.inventoryName}>
                        {item.inventoryName}
                    </MenuItem>
                ))}
            </Select>
            <FormControl sx={{ width: 300 }} align="right">
                <InputLabel>Order Type</InputLabel>
                <Select
                    name="OrderType"
                    value={selectedOrder.OrderType}
                    onChange={handleSelect}
                >
                    <MenuItem value="Sell">Sell</MenuItem>
                    <MenuItem value="Rent">Rent</MenuItem>

                </Select>
            </FormControl>
        </FormControl>
    );
};

export default OrderPage;
