import {
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    TextField,
    DialogTitle,
    Button,
    InputAdornment,
    FormLabel,
    FormControlLabel,
    RadioGroup,
    Radio,
    Box,
    Grid,
    Typography
} from '@mui/material';
import { useEffect, useState } from 'react';
import { AddOrders, getAllOrders, getInventory, updateOrder } from '../Api/Api';

const OrderState = {
    Item: '',
    inventoryId: null,
    price: '',
    Ordertype: '',
    quantity: '',
    customername: '',
    customerlocation: '',
    paymentstatus: '',
    orderstatus: '',
    customerphone: ''
};

const OrderPage = () => {
    const [dataList, setDataList] = useState([]);
    const [selectedOrder, setSelectOrder] = useState(OrderState);

    useEffect(() => {
        const fetchInventory = async () => {
            const result = await getInventory();
            setDataList(result?.data || []);
        };
        fetchInventory();
    }, []);

    useEffect(() => {
        fetchOrder()
    }, [])
    const fetchOrder = async () => {
        // If you don't need to show previous orders in the form, skip setting selectedOrder here
        const result = await getAllOrders();
        // maybe log or set them to a separate state
        console.log(result?.data);
    };
    const handleSelect = (e) => {
        const { name, value } = e.target;

        if (name === 'Item') {
            // Find full inventory object based on inventoryName
            const selectedItem = dataList.find(item => item.inventoryName === value);

            setSelectOrder((prev) => ({
                ...prev,
                Item: selectedItem.inventoryName,
                inventoryId: selectedItem.id,
                price: selectedItem.price,
                unit: selectedItem.unit // optional, for orderTotalAmount
            }));
        } else {
            setSelectOrder((prev) => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handlesave = async () => {
        const {
            inventoryId,
            Item,
            price,
            Ordertype,
            quantity,
            customername,
            customerlocation,
            paymentstatus,
            customerphone,
            unit
        } = selectedOrder;

        // Validation (optional but good)
        if (!inventoryId || !Item || !Ordertype || !quantity || !customername || !customerlocation || !paymentstatus || !customerphone) {
            alert("Please fill in all fields before submitting.");
            return;
        }

        const payload = {
            inventoryid: inventoryId,
            inventoryname: Item,
            unit: unit || 'unit',
            ordertype: Ordertype,
            price: price,
            quantity: quantity,
            customername: customername,
            customerlocation: customerlocation,
            paymentstatus: paymentstatus.toLowerCase(), // safe lowercase
            orderstatus: 'pending',
            customerphone: customerphone
        };


        try {
            const response = await AddOrders(payload);
            console.log("Order Placed:", response.data);
            alert("Order placed successfully!");
        } catch (err) {
            console.error("placing Order Error:", err.response?.data || err.message);
            alert("Failed to place order. Check console for details.");
        }
    };



    return (
        <div>
            <Grid container spacing={2} sx={{ p: 3 }}>
                <Grid item xs={12}>
                    <Typography variant="h6">Product Details</Typography>
                </Grid>
                <br />
                <Grid item xs={12} sm={6}>
                    <FormControl size="small" sx={{ width: 300, mb: 2 }}>
                        <InputLabel id="item-label">Item</InputLabel>
                        <Select
                            labelId="item-label"
                            name="Item"
                            value={selectedOrder.Item} // You missed this
                            onChange={handleSelect}
                        >
                            {dataList?.map((item) => (
                                <MenuItem key={item.id} value={item.inventoryName}>
                                    {item.inventoryName}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </Grid>
                {/* Select Order Type */}
                <Box
                    sx={{
                        border: '1px solid #c4c4c4',
                        borderRadius: '4px',
                        padding: '8px 16px',
                        width: 200,
                        height: 56,
                        display: 'flex',
                        alignItems: 'right'
                    }}
                >
                    <FormControl component="fieldset">
                        <FormLabel component="legend" sx={{ fontSize: 14 }}>Order Type</FormLabel>
                        <RadioGroup
                            row
                            name="Ordertype"
                            value={selectedOrder.Ordertype}
                            onChange={handleSelect}
                        >
                            <FormControlLabel value="sell" control={<Radio />} label="Sell" />
                            <FormControlLabel value="rent" control={<Radio />} label="Rent" />
                        </RadioGroup>
                    </FormControl>
                </Box>

                <br />
                <TextField
                    label="Quantity"
                    name="quantity"
                    type="number"
                    value={selectedOrder.quantity}
                    onChange={handleSelect}
                    sx={{ width: 300 }}
                    InputProps={{
                        endAdornment: <InputAdornment position="end">{selectedOrder.unit}</InputAdornment>
                    }}
                />
                <TextField
                    label="Unit"
                    name="unit"
                    value={selectedOrder.unit || ''}
                    onChange={handleSelect}
                    sx={{ width: 300 }}
                />
                <DialogTitle>Customer Details</DialogTitle>
                <TextField
                    label="Customer Name"
                    name="customername"
                    value={selectedOrder.customername}
                    onChange={handleSelect}
                    sx={{ width: 300 }}
                />
                <TextField
                    label=" Phone Number"
                    name="customerphone"
                    type='number'
                    value={selectedOrder.customerphone}
                    onChange={handleSelect}
                    sx={{ width: 300 }}
                />
                <br />
                <TextField
                    label="Location"
                    name="customerlocation"
                    value={selectedOrder.customerlocation}
                    onChange={handleSelect}
                    sx={{ width: 600 }}
                />
                <br />
                <FormControl sx={{ width: 600 }}>
                    <InputLabel id="paymentstatus">PaymentStatus</InputLabel>
                    <Select
                        labelId="Payment Status"
                        name="paymentstatus"
                        value={selectedOrder.paymentstatus}
                        onChange={handleSelect}
                    >
                        <MenuItem value="completed">Paid</MenuItem>
                        <MenuItem value="pending">pending</MenuItem>
                        <MenuItem value="cancelled">cancelled</MenuItem>

                    </Select>
                    <Button onClick={handlesave}>save</Button>

                </FormControl>
            </Grid>
        </div>
    );
};

export default OrderPage;
