import {
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    TextField,
    DialogTitle,
    Button
} from '@mui/material';
import { useEffect, useState } from 'react';
import { AddOrders, getAllOrders, getInventory, updateOrder } from '../Api/Api';

const OrderState = {
    Item: '',
    inventoryId: null,
    price: '',
    Ordertype: '',
    quantity: '',
    customername: '',
    customerlocation: '',
    paymentstatus: '',
    orderstatus: '',
    customerphone: ''
};

const OrderPage = () => {
    const [dataList, setDataList] = useState([]);
    const [selectedOrder, setSelectOrder] = useState(OrderState);

    useEffect(() => {
        const fetchInventory = async () => {
            const result = await getInventory();
            setDataList(result?.data || []);
        };
        fetchInventory();
    }, []);

    useEffect(() => {
        fetchOrder()
    }, [])
    const fetchOrder = async () => {
        const result = await getAllOrders()
        setSelectOrder(result?.data || [])
    }
    const handleSelect = (e) => {
        const { name, value } = e.target;

        if (name === 'Item') {
            // Find full inventory object based on inventoryName
            const selectedItem = dataList.find(item => item.inventoryName === value);

            setSelectOrder((prev) => ({
                ...prev,
                Item: selectedItem.inventoryName,
                inventoryId: selectedItem.id,
                price: selectedItem.price, // optional, for orderTotalAmount
            }));
        } else {
            setSelectOrder((prev) => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handlesave = async () => {
        const {
            inventoryId,
            Item,
            price,
            Ordertype,
            quantity,
            customername,
            customerlocation,
            paymentstatus,
            customerphone
        } = selectedOrder;

        // Validation (optional but good)
        if (!inventoryId || !Item || !Ordertype || !quantity || !customername || !customerlocation || !paymentstatus || !customerphone) {
            alert("Please fill in all fields before submitting.");
            return;
        }

        const payload = {
            inventoryid: inventoryId,
            inventoryname: Item,
            unit: "unit", // You should pass real unit if available

            price: price,
            quantity: quantity,
            customername: customername,
            orderlocation: customerlocation,
            paymentstatus: paymentstatus,
            orderstatus: "Pending", // or however you're setting it
            customerphone: customerphone
        };

        try {
            const response = await AddOrders(payload);
            console.log("Order Placed:", response.data);
            alert("Order placed successfully!");
        } catch (err) {
            console.error("placing Order Error:", err.response?.data || err.message);
            alert("Failed to place order. Check console for details.");
        }
    };



    return (
        <div>
            {/* Select Item */}
            <DialogTitle>Product Details</DialogTitle>
            <FormControl size="small" sx={{ width: 300, mb: 2 }}>
                <InputLabel id="item-label">Item</InputLabel>
                <Select
                    labelId="item-label"
                    name="Item"
                    value={selectedOrder.Item} // You missed this
                    onChange={handleSelect}
                >
                    {dataList?.map((item) => (
                        <MenuItem key={item.id} value={item.inventoryName}>
                            {item.inventoryName}
                        </MenuItem>
                    ))}
                </Select>
            </FormControl>

            {/* Select Order Type */}
            <FormControl size="small" sx={{ width: 300 }}>
                <InputLabel id="order-type-label">Order Type</InputLabel>
                <Select
                    labelId="order-type-label"
                    name="Ordertype"
                    value={selectedOrder.Ordertype}
                    onChange={handleSelect}
                >
                    <MenuItem value="Sell">Sell</MenuItem>
                    <MenuItem value="Rent">Rent</MenuItem>
                </Select>
            </FormControl>
            <br />
            <TextField
                label="Quantity"
                name="quantity"
                type="number"
                value={selectedOrder.quantity}
                onChange={handleSelect}
                sx={{ width: 600 }}
            />
            <DialogTitle>Customer Details</DialogTitle>
            <TextField
                label="Customer Name"
                name="customername"
                value={selectedOrder.customername}
                onChange={handleSelect}
                sx={{ width: 300 }}
            />
            <TextField
                label=" Phone Number"
                name="customerphone"
                type='number'
                value={selectedOrder.customerphone}
                onChange={handleSelect}
                sx={{ width: 300 }}
            />
            <br />
            <TextField
                label="Location"
                name="customerlocation"
                value={selectedOrder.customerlocation}
                onChange={handleSelect}
                sx={{ width: 600 }}
            />
            <br />
            <FormControl sx={{ width: 600 }}>
                <InputLabel id="PaymentStatus">PaymentStatus</InputLabel>
                <Select
                    labelId="Payment Status"
                    name="paymentstatus"
                    value={selectedOrder.paymentstatus}
                    onChange={handleSelect}
                >
                    <MenuItem value="Paid">Paid</MenuItem>
                    <MenuItem value="Partially Paid">Partially Paid</MenuItem>
                    <MenuItem value="Un Paid">Un Paid</MenuItem>

                </Select>
                <Button onClick={handlesave}>save</Button>

            </FormControl>
        </div>
    );
};

export default OrderPage;
