import {
    Select, MenuItem, FormControl, InputLabel, TextField, DialogTitle,
    Button, InputAdornment, FormLabel, FormControlLabel,
    RadioGroup, Radio, Box
} from '@mui/material';
import { useEffect, useState } from 'react';
import { getInventory } from '../../Api/InventoryApi';
import { AddOrders } from '../../Api/OrderApi';

const OrderState = {
    Item: '',
    inventoryId: null,
    price: '',
    Ordertype: '',
    quantity: '',
    customername: '',
    customerlocation: '',
    paymentstatus: '',
    orderstatus: "ORDER_PENDING",
    customerphone: ''
};

const OrderForm = ({ onOrderPlaced }) => {
    const [dataList, setDataList] = useState([]);
    const [selectedOrder, setSelectOrder] = useState(OrderState);
    const [orderStatusDisabled, setOrderStatusDisabled] = useState(true);

    useEffect(() => {
        const fetchInventory = async () => {
            const result = await getInventory();
            setDataList(result?.data || []);
        };
        fetchInventory();
    }, []);

    const handleSelect = (e) => {
        const { name, value } = e.target;

        if (name === 'Item') {
            const selectedItem = dataList.find(item => item.inventoryName === value);
            setSelectOrder(prev => ({
                ...prev,
                Item: selectedItem.inventoryName,
                inventoryId: selectedItem.id,
                price: selectedItem.price,
                unit: selectedItem.unit,
                availableQuantity: selectedItem.availableQuantity
            }));
        } else if (name === 'paymentstatus') {
            let orderstatus = 'ORDER_INPROGRESS';
            setOrderStatusDisabled(false);

            if (value === 'PAYMENT_COMPLETED') {
                orderstatus = 'ORDER_COMPLETED';
            } else if (value === 'PARTIALLY_PAID' || value === 'PAYMENT_PENDING' || value === 'PAYMENT_FAILED') {
                orderstatus = 'ORDER_INPROGRESS';
            }

            setSelectOrder(prev => ({
                ...prev,
                [name]: value,
                orderstatus: orderstatus
            }));
        } else {
            setSelectOrder(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handlesave = async () => {
        const {
            inventoryId, Item, price, Ordertype, quantity,
            customername, customerlocation, paymentstatus,
            customerphone, unit, orderstatus
        } = selectedOrder;

        if (!inventoryId || !Item || !Ordertype || !quantity || !customername ||
            !customerlocation || !paymentstatus || !customerphone) {
            alert("Please fill in all fields.");
            return;
        }

        const availableQty = Number(selectedOrder.availableQuantity);
        const requestedQty = Number(selectedOrder.quantity);
        if (requestedQty > availableQty) {
            alert("Available quantity is less than requested.");
            return;
        }

        const payload = {
            inventoryid: inventoryId,
            inventoryname: Item,
            unit: unit || 'unit',
            ordertype: Ordertype,
            price: price,
            quantity: quantity,
            customername: customername,
            customerlocation: customerlocation,
            paymentstatus: paymentstatus.toLowerCase(),
            orderstatus: orderstatus,
            customerphone: customerphone
        };

        try {
            const response = await AddOrders(payload);
            alert("Order placed successfully!");
            setSelectOrder(OrderState); // reset form
            setOrderStatusDisabled(true);
            onOrderPlaced(); // notify parent
        } catch (err) {
            console.error("Order error:", err);
            alert("Failed to place order.");
        }
    };

    return (
        <Box sx={{ width: '100%' }}>
            <DialogTitle>Product Details</DialogTitle>
            <FormControl size="small" sx={{ width: 250, mb: 4, marginLeft: 2 }}>
                <InputLabel id="item-label">Item</InputLabel>
                <Select name="Item" value={selectedOrder.Item} onChange={handleSelect}>
                    {dataList?.map((item) => (
                        <MenuItem key={item.id} value={item.inventoryName}>
                            {item.inventoryName}
                        </MenuItem>
                    ))}
                </Select>
            </FormControl>

            <FormControl sx={{ padding: 1, width: 230, marginLeft: 5 }}>
                <FormLabel>Type</FormLabel>
                <RadioGroup
                    row
                    name="Ordertype"
                    value={selectedOrder.Ordertype}
                    onChange={handleSelect}
                >
                    <FormControlLabel value="sell" control={<Radio />} label="Sell" />
                    <FormControlLabel value="rent" control={<Radio />} label="Rent" />
                </RadioGroup>
            </FormControl>

            <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
                <TextField
                    sx={{ width: 250, marginLeft: 2, marginTop: 2 }}
                    label="Quantity"
                    name="quantity"
                    type="number"
                    value={selectedOrder.quantity}
                    onChange={handleSelect}
                    InputProps={{
                        endAdornment: <InputAdornment position="end">{selectedOrder.unit}</InputAdornment>
                    }}
                    helperText={selectedOrder.availableQuantity ? `Available quantity is ${selectedOrder.availableQuantity}` : ''}
                />

                <FormControl sx={{ width: 250, marginLeft: 3, marginTop: 2 }}>
                    <InputLabel>Payment Status</InputLabel>
                    <Select
                        name="paymentstatus"
                        value={selectedOrder.paymentstatus}
                        onChange={handleSelect}
                    >
                        <MenuItem value="PAYMENT_COMPLETED">Payment completed</MenuItem>
                        <MenuItem value="PAYMENT_PENDING">Payment pending</MenuItem>
                        <MenuItem value="PARTIALLY_PAID">Partially paid</MenuItem>
                        <MenuItem value="PAYMENT_FAILED">Payment failed</MenuItem>
                    </Select>
                </FormControl>
            </Box>

            <DialogTitle>Customer Details</DialogTitle>
            <TextField
                label="Customer Name"
                name="customername"
                value={selectedOrder.customername}
                onChange={handleSelect}
                sx={{ width: 250, marginLeft: 2, marginBottom: 2, padding: 1 }}
            />
            <TextField
                label="Phone Number"
                name="customerphone"
                type="number"
                value={selectedOrder.customerphone}
                onChange={handleSelect}
                sx={{ width: 250, marginLeft: 2, marginBottom: 2, padding: 1 }}
            />
            <TextField
                label="Location"
                name="customerlocation"
                value={selectedOrder.customerlocation}
                onChange={handleSelect}
                sx={{ width: 250, marginLeft: 2, marginBottom: 2, padding: 1 }}
            />

            <FormControl sx={{ width: 250, marginLeft: 3, marginTop: 1 }} disabled={orderStatusDisabled}>
                <InputLabel>Order Status</InputLabel>
                <Select
                    name="orderstatus"
                    value={selectedOrder.orderstatus}
                    onChange={handleSelect}
                >
                    <MenuItem value="ORDER_PENDING">Order Pending</MenuItem>
                    <MenuItem value="ORDER_COMPLETED">Order Completed</MenuItem>
                    <MenuItem value="ORDER_INPROGRESS">Order In Progress</MenuItem>
                    <MenuItem value="ORDER_CANCELLED">Order Cancelled</MenuItem>
                </Select>
            </FormControl>

            <Button
                sx={{ width: 250, marginLeft: 2, marginBottom: 2, padding: 1, marginTop: 2 }}
                variant="contained"
                onClick={handlesave}
            >
                Save
            </Button>
        </Box>
    );
};

export default OrderForm;
