import {
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    TextField,
    DialogTitle,
    Button,
    InputAdornment,
    FormLabel,
    FormControlLabel,
    RadioGroup,
    Radio,
    Box
} from '@mui/material';
import { useEffect, useState } from 'react';
import { AddOrders, getAllOrders, getInventory, updateOrder } from '../Api/Api';

const OrderState = {
    Item: '',
    inventoryId: null,
    price: '',
    Ordertype: '',
    quantity: '',
    customername: '',
    customerlocation: '',
    paymentstatus: '',
    orderstatus: '',
    customerphone: ''
};

const OrderPage = () => {
    const [dataList, setDataList] = useState([]);
    const [selectedOrder, setSelectOrder] = useState(OrderState);

    useEffect(() => {
        const fetchInventory = async () => {
            const result = await getInventory();
            setDataList(result?.data || []);
        };
        fetchInventory();
    }, []);

    useEffect(() => {
        fetchOrder()
    }, [])
    const fetchOrder = async () => {
        // If you don't need to show previous orders in the form, skip setting selectedOrder here
        const result = await getAllOrders();
        // maybe log or set them to a separate state
        console.log(result?.data);
    };
    const handleSelect = (e) => {
        const { name, value } = e.target;

        if (name === 'Item') {
            // Find full inventory object based on inventoryName
            const selectedItem = dataList.find(item => item.inventoryName === value);

            setSelectOrder((prev) => ({
                ...prev,
                Item: selectedItem.inventoryName,
                inventoryId: selectedItem.id,
                price: selectedItem.price,
                unit: selectedItem.unit // optional, for orderTotalAmount
            }));
        } else {
            setSelectOrder((prev) => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handlesave = async () => {
        const {
            inventoryId,
            Item,
            price,
            Ordertype,
            quantity,
            customername,
            customerlocation,
            paymentstatus,
            customerphone,
            unit
        } = selectedOrder;

        // Validation (optional but good)
        if (!inventoryId || !Item || !Ordertype || !quantity || !customername || !customerlocation || !paymentstatus || !customerphone) {
            alert("Please fill in all fields before submitting.");
            return;
        }

        const payload = {
            inventoryid: inventoryId,
            inventoryname: Item,
            unit: unit || 'unit',
            ordertype: Ordertype,
            price: price,
            quantity: quantity,
            customername: customername,
            customerlocation: customerlocation,
            paymentstatus: paymentstatus.toLowerCase(), // safe lowercase
            orderstatus: 'pending',
            customerphone: customerphone
        };


        try {
            const response = await AddOrders(payload);
            console.log("Order Placed:", response.data);
            alert("Order placed successfully!");
        } catch (err) {
            console.error("placing Order Error:", err.response?.data || err.message);
            alert("Failed to place order. Check console for details.");
        }
    };



    return (
        <div>

            <Box sx={{ width: 600, border: 2, margin: 2, padding: 5 }}>
                return (
                <Box sx={{ p: 4 }}>
                    <Typography variant="h4" gutterBottom>
                        Order Details
                    </Typography>

                    <Grid container spacing={2}>
                        {/* ID */}
                        <Grid item xs={12} sm={6}>
                            <Paper elevation={3} sx={{ p: 2 }}>
                                <Typography variant="caption">Item</Typography>
                                <Typography>{selectedOrder.Item || '-'}</Typography>
                            </Paper>
                        </Grid>

                        {/* Order Type */}
                        <Grid item xs={12} sm={6}>
                            <Paper elevation={3} sx={{ p: 2 }}>
                                <Typography variant="caption">Order Type</Typography>
                                <Typography>{selectedOrder.Ordertype || '-'}</Typography>
                            </Paper>
                        </Grid>

                        {/* Quantity */}
                        <Grid item xs={12} sm={6}>
                            <Paper elevation={3} sx={{ p: 2 }}>
                                <Typography variant="caption">Quantity</Typography>
                                <Typography>
                                    {selectedOrder.quantity} {selectedOrder.unit || ''}
                                </Typography>
                            </Paper>
                        </Grid>

                        {/* Price */}
                        <Grid item xs={12} sm={6}>
                            <Paper elevation={3} sx={{ p: 2 }}>
                                <Typography variant="caption">Price</Typography>
                                <Typography>{selectedOrder.price || '-'}</Typography>
                            </Paper>
                        </Grid>

                        {/* Customer Name */}
                        <Grid item xs={12} sm={6}>
                            <Paper elevation={3} sx={{ p: 2 }}>
                                <Typography variant="caption">Customer Name</Typography>
                                <Typography>{selectedOrder.customername || '-'}</Typography>
                            </Paper>
                        </Grid>

                        {/* Phone */}
                        <Grid item xs={12} sm={6}>
                            <Paper elevation={3} sx={{ p: 2 }}>
                                <Typography variant="caption">Phone</Typography>
                                <Typography>{selectedOrder.customerphone || '-'}</Typography>
                            </Paper>
                        </Grid>

                        {/* Location */}
                        <Grid item xs={12} sm={6}>
                            <Paper elevation={3} sx={{ p: 2 }}>
                                <Typography variant="caption">Location</Typography>
                                <Typography>{selectedOrder.customerlocation || '-'}</Typography>
                            </Paper>
                        </Grid>

                        {/* Payment Status */}
                        <Grid item xs={12} sm={6}>
                            <Paper elevation={3} sx={{ p: 2 }}>
                                <Typography variant="caption">Payment Status</Typography>
                                <Typography>{selectedOrder.paymentstatus || '-'}</Typography>
                            </Paper>
                        </Grid>
                    </Grid>
                </Box>
                );

            </Box>
        </div>
    );
};

export default OrderPage;
